mapper.select:
SELECT ui.id, ui.template_id, it.name AS item_name, it.icon_url, ui.wear_value, ui.pattern_index, ui.status, ui.get_time FROM user_inventory ui LEFT JOIN base_item_template it ON ui.template_id = it.id WHERE ui.user_id = #{userId}
AND ui.status = #{status}
AND it.type = #{type}
AND it.rarity = #{rarity}
AND it.name LIKE CONCAT('%', #{keyword}, '%')
ORDER BY ui.get_time DESC LIMIT #{offset}, #{pageSize}

SELECT COUNT(*)
FROM user_inventory ui
LEFT JOIN base_item_template it ON ui.template_id = it.id
WHERE ui.user_id = #{userId}

    AND ui.status = #{status}


    AND it.type = #{type}


    AND it.rarity = #{rarity}


    AND it.name LIKE CONCAT('%', #{keyword}, '%')

mapper.update:
UPDATE user_inventory
SET status = #{status}
WHERE id = #{id}
