mapper.select:
SELECT o.id, o.order_no, o.buyer_id, o.seller_id, it.name AS item_name, it.icon_url, o.total_amount, o.status, o.create_time, o.pay_time, o.deliver_time, o.finish_time FROM trade_order o LEFT JOIN user_inventory ui ON o.inventory_id = ui.id LEFT JOIN base_item_template it ON ui.template_id = it.id WHERE o.buyer_id = #{buyerId}
AND o.status = #{status}
ORDER BY o.create_time DESC LIMIT #{offset}, #{pageSize}

SELECT COUNT(*)
FROM trade_order
WHERE buyer_id = #{buyerId}

    AND status = #{status}

SELECT o.id, o.order_no, o.buyer_id, o.seller_id, it.name AS item_name, it.icon_url, o.total_amount, o.status, o.create_time, o.pay_time, o.deliver_time, o.finish_time FROM trade_order o LEFT JOIN user_inventory ui ON o.inventory_id = ui.id LEFT JOIN base_item_template it ON ui.template_id = it.id WHERE o.seller_id = #{sellerId}
AND o.status = #{status}
ORDER BY o.create_time DESC LIMIT #{offset}, #{pageSize}

SELECT COUNT(*)
FROM trade_order
WHERE seller_id = #{sellerId}

    AND status = #{status}

mapper.update:
UPDATE trade_order
SET status = #{status}
WHERE id = #{id}

UPDATE trade_order
SET pay_time = NOW()
WHERE id = #{id}

UPDATE trade_order
SET deliver_time = NOW()
WHERE id = #{id}

UPDATE trade_order
SET finish_time = NOW()
WHERE id = #{id}
